%dw 1.0
%var e1=[{"id":100,"name":"test","dept":"DEV"},{"id":103,"name":"jhjkh","dept":"WA"}]
%var a=[{"eid":100,"city":"HYD","state":"TS"},{"id":101,"city":"HYD","state":"TS"}]
%function getAddress(eid){
	(a[?($.eid==eid)] default {})
}

%output application/json skipNullOn="everything"
---
e1 map ((d)->{
	"emp":d,
	"address":getAddress(d.id)

})
